import _ from 'lodash'
import faker from 'faker'
import React, {Component} from 'react'
import {Search, Grid, Header, Segment} from 'semantic-ui-react'
import {doSearch, clearSearch} from './actions/movies';
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'

const source = _.times(5, () => ({
    title: faker.company.companyName(),
    description: faker.company.catchPhrase(),
    image: faker.internet.avatar(),
    price: faker.finance.amount(0, 100, 2, '$'),
}))

class SearchStandard extends Component {


    constructor(props) {
        super(props)
        this.state = {
            searchInput: ''
        }
    }

    onInputChange = (event) => {

        let {doSearch, clearSearch} = this.props;
        let query = event.target.value.toLowerCase();

        this.setState({
            searchInput: query
        }, () => {
            if (query && query.length > 1) {

                doSearch(query);
            } else {

                clearSearch();
            }
        })
    }

    componentWillMount() {
        this.resetComponent()
    }

    resetComponent = () => this.setState({isLoading: false, results: [], value: ''})

    handleResultSelect = (e, {result}) => this.setState({value: result.title})

    handleSearchChange = (e, {value}) => {
        this.setState({isLoading: true, value})
        console.log('handleSearchChange!!!')
        setTimeout(() => {
            if (this.state.value.length < 1) return this.resetComponent()

            const re = new RegExp(_.escapeRegExp(this.state.value), 'i')
            const isMatch = result => re.test(result.title)

            this.setState({
                isLoading: false,
                results: _.filter(source, isMatch),
            })
        }, 300)
    }

    render() {
        let {searchIsLoading, search} = this.props;


        const {isLoading, value, results} = this.state

        return (
            < Grid>
                < Grid.Column
                    width={6}>
                    < Search
                        loading={isLoading}
                        onResultSelect={this.handleResultSelect}
                        onSearchChange={_.debounce(this.handleSearchChange, 500, {leading: true})}
                        results={results}
                        value={value}
                        {...
                            this.props
                        }
                    />
                    <
                    /Grid.Column>

                    <
                    /Grid>
                    )
                    }
                    }

                    const mapStateToProps = (state) => {
                        return {
                            search:state.movies.search,
                            searchIsLoading:state.movies.searchIsLoading,
                            pathname: state.router.location.pathname,
                            watchlistMovies:state.movies.watchlistMovies
                        }
                    }

                    const mapDispatchToProps = dispatch => bindActionCreators({
                    doSearch,
                    clearSearch
                }, dispatch)
                    export default connect(mapStateToProps, mapDispatchToProps)(SearchStandard);
