import React, {Component} from 'react'
import {Grid, Pagination} from 'semantic-ui-react'

export default class PaginationControlled extends Component {

    constructor(props) {
        super(props)

        this.state = { activePage: 1};
    }


    static getDerivedStateFromProps(nextProps, prevState) {
        if (nextProps.activePage != prevState.activePage) {
            console.log('nextProps: ', nextProps)
            return {...prevState, activePage: nextProps.activePage, onPageChange: nextProps.onPageChange, totalPages: nextProps.totalPages}

        }

        return null;
    }

    handleInputChange = (e, {value}) => this.setState({activePage: value})

    handlePaginationChange = (e, {activePage}) => this.setState({activePage})

    render() {
        const {activePage} = this.state
        const p = this.props;

        console.log('p: ', p);
        return (
            < Grid padded='vertically'
        columns = {3}
        verticalAlign = 'middle'>
            < Grid.Column />
        < Grid.Column >
        < Pagination
        activePage = {p.activePage}
        onPageChange = {p.onPageChange}
        totalPages = {p.totalPages}
        />
        < /Grid.Column>
        < Grid.Column />
        < /Grid>
    )
    }
}
